document.addEventListener("DOMContentLoaded",(function(){new Swiper(".swiper-container",{speed:200,spaceBetween:100,loop:!0,autoplay:!0,pagination:{el:".swiper-pagination",clickable:!0},paginationClickable:!0,navigation:{nextEl:".swiper-next",prevEl:".swiper-prev"}}),document.getElementById("search").addEventListener("click",(function(){document.querySelector("#search-box").classList.toggle("hide")}))}));document.addEventListener("click",function(event){if(!event.target.matches("#search-result")||!event.target.matches(".search")){document.getElementById("search-box-input").value="";document.querySelector("#search-result").classList.remove("show");}},false);